#include "public.h"

//24ms, 57.97%
//统计各种数字出现次数, 然后组合一下

class Solution {
public:
	int threeSumMulti(vector<int>& A, int target) {
		int MOD = 1000000007;
		map<int, int> m;
		for (auto& a : A)
			m[a]++;
		//把键转成vector方便写
		vector<int> uniques;
		for (auto& im : m)
			uniques.push_back(im.first);

		int usize = uniques.size();

		int res = 0;
		//三个数不一样
		for (int i = 0; i < usize - 2; ++i)
		{
			for (int j = i + 1; j < usize - 1; ++j)
			{
				int next = target - uniques[i] - uniques[j];
				if (binary_search(uniques.begin() + j + 1, uniques.end(), next))
				{
					res += m[uniques[i]] * m[uniques[j]] * m[next];
					res %= MOD;
				}
			}
		}

		//俩数一样
		for (int i = 0; i < usize; ++i)
		{
			int next = target - uniques[i] * 2;
			if (next == uniques[i]) continue;
			else if (binary_search(uniques.begin(), uniques.end(), next))
			{
				res += m[uniques[i]] * (m[uniques[i]] - 1) / 2 * m[next];
				res %= MOD;
			}
		}

		//三数一样
		if (target % 3 == 0)
			if (m.find(target / 3) != m.end())
			{
				long long fuckyou = (long long)m[target / 3] * ((long long)m[target / 3] - 1)*((long long)m[target / 3] - 2) / 6;
				fuckyou %= MOD;
				res += fuckyou;
			}
		return res;
	}
};

